        [Test]
        public void FITSHeaderCard_NullTest() {
            var key = "SOME";

            var sut = new FITSHeaderCard(key, null, null);

            sut.Key.Should().Be(key);
            sut.Value.Should().Be("''                  ");
            sut.Comment.Should().Be(string.Empty);
        }

